interface RuleInfo {
    name: string;
    ruleId: string;
    originalId?: string;
    entry: string;
    docsEntry: string;
    meta?: RuleMeta;
}
interface PackageInfo {
    name: string;
    pkgId: string;
    shortId: string;
    rules: RuleInfo[];
    path: string;
}
interface RuleMeta {
    fixable?: 'code' | 'whitespace' | null;
    docs?: {
        description?: string;
        recommended?: boolean | 'stylistic';
    };
}

declare const packages: Readonly<PackageInfo[]>;
declare const rules: Readonly<RuleInfo[]>;

export { type PackageInfo, type RuleInfo, type RuleMeta, packages, rules };
